mapper.update:
UPDATE feed
feed_update_date = #{feedUpdateDate}, feed_content = #{feedContent}, feed_like_count = #{feedLikeCnt}, feed_share_count = #{feedShareCnt}, user_reply_count = #{feedReplyCnt}, user_picture = #{feedPicture}, feed_original_seq = #{feedOriginalSeq},
WHERE feed_seq = #{feedSeq}

	UPDATE feed
	SET feed_like_count = feed_like_count + #{feedLikeCnt}
    WHERE feed_seq = #{feedSeq}

	UPDATE feed
	SET feed_reply_count = feed_reply_count + #{feedReplyCnt}
    WHERE feed_seq = #{feedSeq}

	UPDATE feed
	SET feed_share_count = feed_share_count + #{feedShareCnt}
    WHERE feed_seq = #{feedSeq}

mapper.select:
SELECT B.feed_seq, B.user_seq, B.feed_regdate, B.feed_content, B.feed_like_count, B.feed_update_date, B.feed_reply_count, B.feed_share_count, B.feed_picture, B.feed_original_seq FROM (SELECT /*+INDEX_DESC(A feed_seq)*/ ROWNUM AS rnum, A.feed_seq, A.user_seq, A.feed_regdate, A.feed_content, A.feed_like_count, A.feed_update_date, A.feed_reply_count, A.feed_share_count, A.feed_picture, A.feed_original_seq FROM (SELECT feed_seq, user_seq, feed_regdate, feed_content, feed_like_count, feed_update_date, feed_reply_count, feed_share_count, feed_picture, feed_original_seq FROM feed WHERE
feed_content LIKE '%' || #{keyword} || '%'
AND
user_seq = #{userSeq}
ORDER BY feed_seq DESC) A WHERE ROWNUM <= #{endNum}) B WHERE B.rnum >= #{startNum}
